class BigQueryRepository:
    def __init__(self, client: bigquery.Client):
        self.bq_client = client

    def run_query(self, query: str, query_params: list = None):
        job_config = bigquery.QueryJobConfig(
            query_parameters=query_params or [],
            labels={"application": "bigquery-insight-dashboard"},
        )
        return self.bq_client.query(query, job_config=job_config).result()

# Improved code:
class BigQueryRepository:
    def __init__(self, client: bigquery.Client, cache_duration: int = 3600):  # Cache duration in seconds
        self.bq_client = client
        self.cache = {}
        self.cache_duration = cache_duration

    def run_query(self, query: str, query_params: list = None):
        # Generate a unique key for the query and parameters
        cache_key = hash((query, tuple(sorted(query_params or []))))

        if cache_key in self.cache and time.time() - self.cache[cache_key]['timestamp'] < self.cache_duration:
            # Return cached results
            print("Retrieving data from cache")
            return self.cache[cache_key]['data']
        else:
            # Run the query
            print("Running query in BigQuery")
            job_config = bigquery.QueryJobConfig(
                query_parameters=query_params or [],
                labels={"application": "bigquery-insight-dashboard"},
            )
            results = self.bq_client.query(query, job_config=job_config).result()

            # Cache the results
            self.cache[cache_key] = {'data': results, 'timestamp': time.time()}
            return results
