    if (getifaddrs(&ifaddr) == -1) {
        return gateway; // Error occurred, return default gateway
    }

    for (ifa = ifaddr; ifa != NULL; ifa = ifa->ifa_next) {
        if (ifa->ifa_addr == NULL) {
            continue;
        }

        // IPv4 check
        if (ifa->ifa_addr->sa_family == AF_INET) {
            struct sockaddr_in *sa = (struct sockaddr_in *)ifa->ifa_addr;
            char addr_str[INET_ADDRSTRLEN];
            if (inet_ntop(AF_INET, &(sa->sin_addr), addr_str, INET_ADDRSTRLEN) != NULL) {
                if (strcmp(addr_str, "127.0.0.1") != 0) { // Exclude localhost
                    gateway.ipv4 = addr_str;
                }
            }
        }

        // IPv6 check
        if (ifa->ifa_addr->sa_family == AF_INET6) {
            struct sockaddr_in6 *sa6 = (struct sockaddr_in6 *)ifa->ifa_addr;
            char addr_str[INET6_ADDRSTRLEN];
            if (inet_ntop(AF_INET6, &(sa6->sin6_addr), addr_str, INET6_ADDRSTRLEN) != NULL) {
                if (strcmp(addr_str, "::1") != 0) { // Exclude localhost
                    gateway.ipv6 = addr_str;
                }
            }
        }
    }

    freeifaddrs(ifaddr);
    return gateway;
}

} // namespace Network
} // namespace Util
