        {
            if (TryAcquirePipe())
            {
                BuildAvaloniaApp().StartWithClassicDesktopLifetime(args);
            }
            else
            {
                Console.WriteLine("Another instance is already running.");
                SendSignalToExistingInstance();
            }
        }

        private static bool TryAcquirePipe()
        {
            try
            {
                using (var pipeServer = new NamedPipeServerStream(PipeName, PipeTransmissionMode.Byte))
                {
                    pipeServer.WaitForConnection(1000); // Wait for a bit to see if another instance connects
                    return true;
                }
            }
            catch (Exception)
            {
                return false;
            }
        }

