```dart
// Model
class CounterModel {
  int _count = 0;
  int get count => _count;
  void increment() => _count++;
  void decrement() => _count--;
}

// View
class CounterView extends StatelessWidget {
  final CounterController controller;

  const CounterView({Key? key, required this.controller}) : super(key: key);

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(title: const Text('MVC Counter')),
      body: Center(
        child: Text('Count: ${controller.model.count}'),
      ),
      floatingActionButton: Row(
        mainAxisAlignment: MainAxisAlignment.end,
        children: [
          FloatingActionButton(
            onPressed: controller.decrement,
            child: const Icon(Icons.remove),
          ),
          const SizedBox(width: 16),
          FloatingActionButton(
            onPressed: controller.increment,
            child: const Icon(Icons.add),
          ),
        ],
      ),
    );
  }
}

// Controller
class CounterController {
  final CounterModel model = CounterModel();
  final CounterView view;

  CounterController({required this.view});

  void increment() {
    model.increment();
    view.setState(() {}); // Trigger view update
  }

  void decrement() {
    model.decrement();
    view.setState(() {}); // Trigger view update
  }
}
