```dart
// Model
class Task {
  String title;
  bool isCompleted;

  Task(this.title, this.isCompleted);

  void toggleCompletion() {
    isCompleted = !isCompleted;
  }
}

class ToDoModel {
  final List<Task> tasks = [];

  void addTask(String title) {
    tasks.add(Task(title, false));
  }

  void removeTask(Task task) {
    tasks.remove(task);
  }
