function validateEmail(email) {
  if (!email) {
    return false; // Email cannot be empty
  }

  // Check for @ symbol
  if (email.indexOf('@') === -1) {
    return false;
  }

  // Split into local and domain parts
  const parts = email.split('@');
  if (parts.length !== 2) {
    return false; // Should only have one @
  }

  const local = parts[0];
  const domain = parts[1];

  // Check for at least one character before @
  if (local.length === 0) {
    return false;
  }

  // Check for . in domain
  if (domain.indexOf('.') === -1) {
    return false;
  }

  // Check for at least one character after the last .
  if (domain.lastIndexOf('.') === domain.length - 1) {
    return false;
  }

  return true;
}

