import random

def create_individual(genes):
    return {gene: random.choice(alleles) for gene, alleles in genes.items()}


def create_population(size, genes):
    return [create_individual(genes) for _ in range(size)]


def reproduce(parent1, parent2):
    child = {}
    for gene in parent1:
        child[gene] = random.choice([parent1[gene], parent2[gene]])
    return child


def calculate_phenotype(individual, phenotype_rules):
    phenotype = {}
    for trait, rule in phenotype_rules.items():
        gene1, gene2 = rule['genes']
        if individual[gene1] == rule['dominant'][0] or individual[gene2] == rule['dominant'][1]:
            phenotype[trait] = rule['dominant_phenotype']
        else:
            phenotype[trait] = rule['recessive_phenotype']
    return phenotype

