            grabbedRigidbody.AddForce(transform.forward * throwForce.magnitude, ForceMode.Impulse); //Apply force in the forward direction

            // Reset the grabbed object and Rigidbody references
            grabbedObject = null;
            grabbedRigidbody = null;

